package com.tencent.weixin.util;import javax.servlet.http.HttpServletRequest;/*** @ClassName: WebUtil* @Description: WEB相关工具类* @author jdkleo * @Workemail zhangyang33@sinopharm.com* @date 2015年11月24日 下午4:36:30**/ public class WebUtil {    /**     * 获得客户端IP     * @param request     * @return 客户端IP     */    public static String getIp(HttpServletRequest request) {        String ip = request.getHeader("x-forwarded-for");        if (ip == null || ip.length() == 0 || "unknown".equalsIgnoreCase(ip)) {            ip = request.getHeader("Proxy-Client-IP");        }        if (ip == null || ip.length() == 0 || "unknown".equalsIgnoreCase(ip)) {            ip = request.getHeader("WL-Proxy-Client-IP");        }        if (ip == null || ip.length() == 0 || "unknown".equalsIgnoreCase(ip)) {            ip = request.getRemoteAddr();        }        if ("0:0:0:0:0:0:0:1".equals(ip) || null == ip){            ip = IpUtil.getLocalIP();        }        return ip;    }}